import React, {useEffect, useState} from 'react';
import '../styles/Home.css';

function Home(){
    var [url,setUrl]=useState("");
    var [error, setError] = useState("");
    var [isDomReady, setIsDomReady] = useState("");
    useEffect(() =>{
        setIsDomReady(true);
    },[]);
        const handleLoadUrl = () =>{
            if(!isDomReady) return;
            const urlPattern = /(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&//=]*)/g;
            if(!urlPattern.test(url)){
                console.log("Please Enter a valid URL");
            }
            else{
                setError("");
            }
        };
    return(
    <div className="content">
        <h1 className="title">Web Scraper</h1>
        <div className="url-box-container">
            <input className="url-box" 
            placeholder="Enter URL to Scrape" 
            value={url} 
            onChange={(e) => setUrl(e.target.value)}></input>
            <button className='load-url-button' onClick={handleLoadUrl}>Load URL</button>
            {error && <div className='error-text'>{error}</div>}
        </div>    
      </div> 
    )   
}

export default Home;
