import React, { useState } from 'react';
import '../styles/Home.css';

function Home() {
  const [url, setUrl] = useState("");
  const [error, setError] = useState("");

  const handleLoadUrl = () => {
    const urlPattern = /(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&//=]*)/g;

    if (!urlPattern.test(url)) {
      setError("❌ Please enter a valid URL");
    } else {
      setError("");
      console.log("✅ Valid URL entered:", url);
      // You can continue with loading the URL here
    }
  };

  return (
    <div className="content">
      <h1 className="title">Web Scraper</h1>
      <div className="url-box-container">
        <input
          className="url-box"
          placeholder="Enter URL to Scrape"
          value={url}
          onChange={(e) => {
            setUrl(e.target.value);
            setError(""); // Clear error while typing
          }}
        />
        <button className="load-url-button" onClick={handleLoadUrl}>
          Load URL
        </button>

        {error && <div className="error-text">{error}</div>}
      </div>
    </div>
  );
}

export default Home;
